import { DidResolutionError } from "./DidResolutionError.js";

/**
 * The DID supplied to the DID resolution function does not conform to valid syntax.
 *
 * @see https://www.w3.org/TR/did-core/#did-resolution-metadata
 */
export class InvalidDidError extends DidResolutionError {
  static code = "invalidDid";

  constructor(message: string) {
    super(InvalidDidError.code, message);
    this.name = "InvalidDidError";
  }
}
