import { DidResolutionError } from "./DidResolutionError.js";

/**
 * This error code is returned if the representation requested via the accept input metadata
 * property is not supported by the DID method and/or DID resolver implementation.
 *
 * @see https://www.w3.org/TR/did-core/#did-resolution-metadata
 */
export class RepresentationNotSupportedError extends DidResolutionError {
  static code = "representationNotSupported";

  constructor(message: string) {
    super(RepresentationNotSupportedError.code, message);
    this.name = "RepresentationNotSupportedError";
  }
}
