import { Buffer } from "buffer";

export const add0xPrefix = (str: string): string =>
  str.startsWith("0x") ? str : `0x${str}`;

export const remove0xPrefix = (str: string): string =>
  str.startsWith("0x") ? str.slice(2) : str;

export const hexStringToBase64Url = (hexString: string): string => {
  const base64 = Buffer.from(hexString, "hex").toString("base64");
  return base64.replace(/\+/g, "-").replace(/\//g, "_").replace(/=/g, "");
};
